contracts/Bounty.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './DeadlineUtils.sol';

enum BountyStatus {
	Ongoing,
	Completed, // Worker can withdraw their pay,
	WorkerHasBeenPaid, // Worker has withdrawn their pair, ending the investment opportunity
	Cancelled // Investors can withdraw their investment
}

struct BountyMetadata {
	uint128 workerStablePay; // Amount of stablecoin to pay worker
	uint128 workerNativePay; // Amount of NT to pay worker
	uint96 exchangeRate; // How much stable is required to buy 1 NT
	address stablecoin; // stable contract address
	address nativeToken; // NT contract address
	uint128 dailyYieldPercentage; // APR/365 in basis point, e.g 0.27% = 27
	address worker;
	address recruiter;
	BountyStatus status;
	uint256 workerDeadline; // In seconds since Unix Epoch
	uint128 initialWorkerStablePay;
	uint128 initialWorkerNativePay;
	uint256 creationDate;
	uint256 workerPaidAt; // 0 if not paid
}

contracts/DeadlineUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './MathUtils.sol';

library DeadlineUtils {
	error WorkerDeadlineMustBeAfterCreationDate();
	error DateIsInThePast();

	function revertIfWorkerDeadlineIsInvalid(uint256 bountyCreationDate, uint256 workerDeadline) public pure {
		if (workerDeadline <= bountyCreationDate) {
			revert WorkerDeadlineMustBeAfterCreationDate();
		}
	}

	/// Calculates the deadline for the investment opportunity
	/// @param bountyCreationDate the time the bounty was created
	/// @param workerDeadline the deadline where the worker should be completed
	/// @return investmentOpportunityPercentage added to to the workerDeadline to calculate the investment opportunity deadline
	function getInvestmentOpportunityDeadline(
		uint256 bountyCreationDate,
		uint256 workerDeadline,
		uint256 investmentOpportunityPercentage
	) public pure returns (uint256) {
		revertIfWorkerDeadlineIsInvalid(bountyCreationDate, workerDeadline);

		uint256 duration = workerDeadline - bountyCreationDate;
		uint256 additionalDuration = MathUtils.calculatePercentage(duration, investmentOpportunityPercentage);
		uint256 investmentOpportunityDuration = duration + additionalDuration;

		return bountyCreationDate + investmentOpportunityDuration;
	}

	function getDaysBeforeDate(uint256 nowAsUnixSeconds, uint256 dateAsUnixSeconds) public pure returns (uint128) {
		if (dateAsUnixSeconds < nowAsUnixSeconds) {
			revert DateIsInThePast();
		}
		return uint128((dateAsUnixSeconds - nowAsUnixSeconds) / 1 days);
	}

	function getDaysBeforeInvestmentOpportunityDeadline(
		uint256 bountyCreationDate,
		uint256 nowAsUnixSeconds,
		uint256 workerDeadline,
		uint256 investmentOpportunityPercentage
	) external pure returns (uint128) {
		uint256 investmentOpportunityDeadline = getInvestmentOpportunityDeadline(
			bountyCreationDate,
			workerDeadline,
			investmentOpportunityPercentage
		);
		return getDaysBeforeDate(nowAsUnixSeconds, investmentOpportunityDeadline);
	}

	function isWorkerDeadlineExpired(uint256 workerDeadline) external view returns (bool) {
		// TODO: Double check if ok to check it like this
		return workerDeadline < block.timestamp;
	}
}

contracts/MathUtils.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

library MathUtils {

    /// Calculates the percentage of a value. E.g 10% of 200 will return 20
    /// @param value the value to calculate the percentage of
    /// @param percentage in basis point, e.g 0.27% = 27
	/// @return PercentageValue of the value
	function calculatePercentage(uint256 value, uint256 percentage) external pure returns (uint256) {
		return (value * percentage) / 10000;
	}
}

contracts/WorkFi.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import './IWorkFi.sol';
import './Bounty.sol';
import './BountyUtils.sol';
import './MathUtils.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Address.sol';

// TODO: If worker not found after worker deadline, everyone egts refunded but yield goes to investors because of the risk
// TODO: tests for unhappy paths (errors etc)
// TODO: Check arithmetic operations
// TODO Security: double check all type conversions
contract WorkFi is IWorkFi, ReentrancyGuard, Ownable {
	using Address for address payable;
	using BountyUtils for BountyMetadata;

	BountyMetadata[] bounties;
	mapping(uint256 => mapping(address => InvestmentMetadata[])) investments;

	mapping(address => bool) whitelistedStablecoins;

	address constant ETH_ADDRESS = address(0);
	uint256 public constant INVESTMENT_OPPORTUNITY_DURATION_PERCENTAGE_IN_BASIS_POINT = 30_00; // 30%

	function addStablecoinToWhitelist(address stablecoin) external override onlyOwner {
		whitelistedStablecoins[stablecoin] = true;
		emit StablecoinAddedToWhitelist(stablecoin);
	}

	function removeStablecoinFromWhitelist(address stablecoin) external override onlyOwner {
		whitelistedStablecoins[stablecoin] = false;
		emit StablecoinRemovedFromWhitelist(stablecoin);
	}

	function acceptWorker(uint256 bountyId, address worker) external override {
		if (bountyId > bounties.length) {
			revert BountyDoesNotExist();
		}
		if (worker == address(0)) {
			revert WorkerCannotBeZero();
		}
		BountyMetadata storage bounty = bounties[bountyId - 1];
		if (DeadlineUtils.isWorkerDeadlineExpired(bounty.workerDeadline)) {
			revert WorkerDeadlineExpired();
		}
		if (bounty.status != BountyStatus.Ongoing) {
			revert BountyIsNotOngoing();
		}
		if (bounty.worker != address(0)) {
			revert AWorkerWasAlreadyAccepted();
		}

		bounty.worker = worker;
		emit WorkerAccepted(bountyId, worker);
	}

	function createBounty(
		uint128 workerStablePay,
		uint128 workerNativePay,
		uint96 exchangeRate,
		address nativeToken,
		address stablecoin,
		uint128 dailyYieldPercentage,
		uint256 workerDeadline
	) external payable override nonReentrant returns (uint256) {
		uint128 yieldPool = _calculateYieldPool(workerNativePay, dailyYieldPercentage, block.timestamp, workerDeadline);
		uint128 amountOfNtOnBounty = workerNativePay + yieldPool;
		if (nativeToken == ETH_ADDRESS) {
			if (msg.value != amountOfNtOnBounty) {
				revert ThisAmountOfEthIsRequired(amountOfNtOnBounty);
			}
		} else {
			if (msg.value > 0) {
				revert ValueShouldBeZeroIfNotPayingWithEth();
			}
		}

		if (!whitelistedStablecoins[stablecoin]) {
			revert NotAWhitelistedStablecoin();
		}
		DeadlineUtils.revertIfWorkerDeadlineIsInvalid(block.timestamp, workerDeadline);

		BountyMetadata memory bounty = BountyMetadata({
			workerStablePay: workerStablePay,
			workerNativePay: workerNativePay,
			exchangeRate: exchangeRate,
			stablecoin: stablecoin,
			nativeToken: nativeToken,
			dailyYieldPercentage: dailyYieldPercentage,
			worker: address(0),
			recruiter: msg.sender,
			status: BountyStatus.Ongoing,
			workerDeadline: workerDeadline,
			initialWorkerStablePay: workerStablePay,
			initialWorkerNativePay: workerNativePay,
			creationDate: block.timestamp,
			workerPaidAt: 0
		});

		bounties.push(bounty);

		emit BountyCreated(bounties.length, msg.sender);

		if (nativeToken != ETH_ADDRESS) {
			// TODO: Check if supports interface for security ?
			IERC20 token = IERC20(nativeToken);
			// TODO: Use Escrow or other Payment contract ? https://docs.openzeppelin.com/contracts/2.x/api/payment
			token.transferFrom(msg.sender, address(this), amountOfNtOnBounty);
		}

		if (workerStablePay > 0) {
			IERC20 stablecoinContract = IERC20(bounty.stablecoin);
			stablecoinContract.transferFrom(msg.sender, address(this), workerStablePay);
		}

		return bounties.length;
	}

	function invest(uint256 bountyId, uint128 stableAmount) external override {
		if (bountyId > bounties.length) {
			revert BountyDoesNotExist();
		}
		BountyMetadata storage bounty = bounties[bountyId - 1];
		if (bounty.isInvestmentOpportunityClosed(INVESTMENT_OPPORTUNITY_DURATION_PERCENTAGE_IN_BASIS_POINT)) {
			revert InvestmentOpportunityIsClosed();
		}
		if (msg.sender == bounty.worker) {
			revert WorkerCannotInvest();
		}
		if (msg.sender == bounty.recruiter) {
			revert RecruiterCannotInvest();
		}
		uint128 maxPossibleInvestment = getStableNeeded(bounty.workerNativePay, bounty.exchangeRate);
		if (stableAmount > maxPossibleInvestment) {
			revert MaxInvestmentExceeded(maxPossibleInvestment);
		}
		uint128 workerNativePayGoingToTheInvestor = getNativePayFromStable(stableAmount, bounty.exchangeRate);
		bounty.workerNativePay -= workerNativePayGoingToTheInvestor;
		bounty.workerStablePay += stableAmount;

		investments[bountyId][msg.sender].push(
			InvestmentMetadata({stableAmount: stableAmount, creationDate: block.timestamp})
		);

		emit Invested(bountyId, msg.sender, stableAmount);
		// TODO: Use Escrow or other Payment contract ? https://docs.openzeppelin.com/contracts/2.x/api/payment
		// TODO: What happens if a stable gets removed from the whitelist while a task is using it as stable ?
		IERC20 stablecoinContract = IERC20(bounty.stablecoin);
		stablecoinContract.transferFrom(msg.sender, address(this), stableAmount);
	}

	function acceptWorkerPayment(uint256 bountyId) external override nonReentrant {
		if (bountyId > bounties.length) {
			revert BountyDoesNotExist();
		}
		BountyMetadata storage bounty = bounties[bountyId - 1];
		if (bounty.status == BountyStatus.WorkerHasBeenPaid) {
			revert WorkerHasBeenPaid();
		}
		if (msg.sender != bounty.worker) {
			revert NotTheWorker();
		}

		uint128 workerNativePay = bounty.workerNativePay;
		uint128 workerStablePay = bounty.workerStablePay;

		bounty.status = BountyStatus.WorkerHasBeenPaid;
		bounty.setWorkerPayToZero();
		bounty.workerPaidAt = block.timestamp;

		emit WorkerPaymentAccepted(bountyId, workerNativePay, workerStablePay);

		sendNativeToken(bounty.nativeToken, bounty.worker, workerNativePay);
		IERC20 stablecoin = IERC20(bounty.stablecoin);
		stablecoin.transfer(bounty.worker, workerStablePay);
	}

	function acceptInvestorPayment(uint256 bountyId) external override nonReentrant {
		if (bountyId > bounties.length) {
			revert BountyDoesNotExist();
		}
		BountyMetadata storage bounty = bounties[bountyId - 1];
		if (!bounty.isInvestmentOpportunityClosed(INVESTMENT_OPPORTUNITY_DURATION_PERCENTAGE_IN_BASIS_POINT)) {
			revert InvestmentOpportunityIsStillOpen();
		}
		if (investments[bountyId][msg.sender].length == 0) {
			revert NotAnInvestor();
		}

		// TODO: Max amount of investments to prevent large loops ? Only blocks the investor itself though. Or maybe allow interacting with 1 investment at a time or batches of them. Or maybe allow interacting with 1 investment at a time or batches of them. Or maybe allow interacting with 1 investment at a time or batches of them
		uint128 payment = 0;
		uint256 investmentOpportunityEndDate = bounty.workerPaidAt > 0
			? bounty.workerPaidAt
			: DeadlineUtils.getInvestmentOpportunityDeadline(
				bounty.creationDate,
				bounty.workerDeadline,
				INVESTMENT_OPPORTUNITY_DURATION_PERCENTAGE_IN_BASIS_POINT
			);
		for (uint256 i = 0; i < investments[bountyId][msg.sender].length; i++) {
			uint128 stableAmount = investments[bountyId][msg.sender][i].stableAmount;
			uint256 creationDate = investments[bountyId][msg.sender][i].creationDate;
			uint128 workerNativePayGoingToTheInvestor = getNativePayFromStable(stableAmount, bounty.exchangeRate);
			uint128 daysInvested = DeadlineUtils.getDaysBeforeDate(creationDate, investmentOpportunityEndDate);

			payment +=
				workerNativePayGoingToTheInvestor +
				_calculateTotalYield(workerNativePayGoingToTheInvestor, bounty.dailyYieldPercentage, daysInvested);
		}
		delete investments[bountyId][msg.sender];

		emit InvestorPaymentAccepted(bountyId, payment);

		sendNativeToken(bounty.nativeToken, msg.sender, payment);
	}

	function sendNativeToken(
		address tokenAddress,
		address to,
		uint256 amount
	) private {
		if (tokenAddress != ETH_ADDRESS) {
			IERC20 token = IERC20(tokenAddress);
			token.transfer(to, amount);
		} else {
			payable(to).sendValue(amount);
		}
	}

	function markBountyAsCompleted(uint256 bountyId) external override {
		if (bountyId > bounties.length) {
			revert BountyDoesNotExist();
		}

		BountyMetadata storage bounty = bounties[bountyId - 1];

		if (msg.sender != bounty.recruiter) {
			revert NotRecruiter();
		}

		if (bounty.worker == address(0)) {
			revert WorkerCannotBeZero();
		}

		if (bounty.status != BountyStatus.Ongoing) {
			revert BountyIsNotOngoing();
		}

		bounty.status = BountyStatus.Completed;

		emit BountyCompleted(bountyId);
	}

	// TODO: Write tests
	function cancelBounty(uint256 bountyId) external override {
		if (bountyId > bounties.length) {
			revert BountyDoesNotExist();
		}

		BountyMetadata storage bounty = bounties[bountyId - 1];
		if (msg.sender != bounty.recruiter) {
			revert NotRecruiter();
		}
		if (bounty.status != BountyStatus.Ongoing) {
			revert BountyIsNotOngoing();
		}

		// get stable the recruiter has put on the bounty
		// get native token the recruiret has put on the bounty
		// refund that
		bounty.status = BountyStatus.Cancelled;
		bounty.setWorkerPayToZero();

		uint128 yieldPool = _calculateYieldPool(
			bounty.initialWorkerNativePay,
			bounty.dailyYieldPercentage,
			block.timestamp,
			bounty.workerDeadline
		);

		emit BountyCancelled(bountyId);

		sendNativeToken(bounty.nativeToken, bounty.recruiter, bounty.initialWorkerNativePay + yieldPool);
		IERC20 stablecoin = IERC20(bounty.stablecoin);
		stablecoin.transfer(bounty.recruiter, bounty.initialWorkerStablePay);
	}

	// TODO: Write tests
	function withdrawInvestments(uint256 bountyId) external override {
		BountyMetadata storage bounty = bounties[bountyId - 1];
		if (bounty.status != BountyStatus.Cancelled) {
			revert BountyIsNotCancelled();
		}
		if (investments[bountyId][msg.sender].length == 0) {
			revert NotAnInvestor();
		}

		// TODO: Max amount of investments to prevent large loops ? Only blocks the investor itself though. Or maybe allow interacting with 1 investment at a time or batches of them. Or maybe allow interacting with 1 investment at a time or batches of them. Or maybe allow interacting with 1 investment at a time or batches of them
		uint128 stableInvestment = 0;
		for (uint256 i = 0; i < investments[bountyId][msg.sender].length; i++) {
			stableInvestment += investments[bountyId][msg.sender][i].stableAmount;
		}
		delete investments[bountyId][msg.sender];

		emit InvestmentsWithdrawn(bountyId);

		IERC20 stablecoinContract = IERC20(bounty.stablecoin);
		stablecoinContract.transfer(msg.sender, stableInvestment);
	}

	/////////////////
	// VIEW FUNCTIONS
	/////////////////

	function getBounty(uint256 bountyId) external view override returns (BountyMetadata memory) {
		return bounties[bountyId - 1];
	}

	function getAmountOfInvestments(uint256 bountyId) external view override returns (uint256) {
		return investments[bountyId][msg.sender].length;
	}

	function getInvestment(uint256 bountyId, uint256 investmentId)
		external
		view
		override
		returns (InvestmentMetadata memory)
	{
		if (bountyId > bounties.length) {
			revert BountyDoesNotExist();
		}
		return investments[bountyId][msg.sender][investmentId - 1];
	}

	/////////////////
	// PURE FUNCTIONS
	/////////////////

	function calculateYieldPool(
		uint128 workerNativePay,
		uint128 dailyYieldPercentage,
		uint256 bountyCreationDate,
		uint256 workerDeadline
	) external pure override returns (uint128) {
		return _calculateYieldPool(workerNativePay, dailyYieldPercentage, bountyCreationDate, workerDeadline);
	}

	function _calculateYieldPool(
		uint128 workerNativePay,
		uint128 dailyYieldPercentage,
		uint256 bountyCreationDate,
		uint256 workerDeadline
	) private pure returns (uint128) {
		uint128 investmentOpportunityDays = DeadlineUtils.getDaysBeforeInvestmentOpportunityDeadline(
			bountyCreationDate,
			bountyCreationDate,
			workerDeadline,
			INVESTMENT_OPPORTUNITY_DURATION_PERCENTAGE_IN_BASIS_POINT
		);
		return _calculateTotalYield(workerNativePay, dailyYieldPercentage, investmentOpportunityDays);
	}

	function calculateTotalYield(
		uint128 initialValue,
		uint128 dailyYieldPercentage,
		uint128 daysBeforeInvestmentOpportunityCloses
	) external pure override returns (uint128) {
		return _calculateTotalYield(initialValue, dailyYieldPercentage, daysBeforeInvestmentOpportunityCloses);
	}

	function _calculateTotalYield(
		uint128 initialValue,
		uint128 dailyYieldPercentage,
		uint128 daysBeforeInvestmentOpportunityCloses
	) private pure returns (uint128) {
		return
			uint128(
				MathUtils.calculatePercentage(initialValue, dailyYieldPercentage) * daysBeforeInvestmentOpportunityCloses
			);
	}

	function getStableNeeded(uint128 nativePay, uint128 exchangeRate) private pure returns (uint128) {
		return nativePay * exchangeRate;
	}

	function getNativePayFromStable(uint128 stableAmount, uint128 exchangeRate) private pure returns (uint128) {
		return stableAmount / exchangeRate;
	}
}

contracts/IWorkFi.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import './Bounty.sol';

interface IWorkFi {
	struct InvestmentMetadata {
		uint128 stableAmount;
		uint256 creationDate;
	}

	event BountyCreated(uint256 indexed bountyId, address indexed recruiter);
	event WorkerAccepted(uint256 indexed bountyId, address indexed worker);
	event Invested(uint256 indexed bountyId, address indexed investor, uint128 amount);
	event StablecoinAddedToWhitelist(address indexed stablecoin);
	event StablecoinRemovedFromWhitelist(address indexed stablecoin);
	event InvestmentsWithdrawn(uint256 indexed bountyId);
	event BountyCancelled(uint256 indexed bountyId);
	event BountyCompleted(uint256 indexed bountyId);
	event InvestorPaymentAccepted(uint256 indexed bountyId, uint128 payment);
	event WorkerPaymentAccepted(uint256 indexed bountyId, uint128 nativePay, uint128 stablePay);

	error BountyDoesNotExist();
	error WorkerCannotBeZero();
	error NotRecruiter();
	error WorkerCannotInvest();
	error RecruiterCannotInvest();
	error WorkerHasBeenPaid();
	error NotTheWorker();
	error NotAnInvestor();
	error AWorkerWasAlreadyAccepted();
	error ThisAmountOfEthIsRequired(uint128 required);
	error ValueShouldBeZeroIfNotPayingWithEth();
	error NotAWhitelistedStablecoin();
	error MaxInvestmentExceeded(uint128 maxPossibleAmount);
	error InvestmentOpportunityIsStillOpen();
	error InvestmentOpportunityIsClosed();
	error WorkerDeadlineExpired();
	error BountyIsNotOngoing();
	error BountyIsNotCancelled();

	/////////////////
	// WRITE FUNCTIONS
	/////////////////

	/// Called by the owner to add a stablecoin to the whitelist. Only whitelisted stablecoins can be used in bounties.
	function addStablecoinToWhitelist(address stablecoin) external;

	/// Called by the owner to remove a stablecoin to the whitelist. Only whitelisted stablecoins can be used in bounties.
	function removeStablecoinFromWhitelist(address stablecoin) external;

	/// Called by the recruiter to accept an address as a valid worker.
	function acceptWorker(uint256 bountyId, address worker) external;

	/// Called by recruiter in creation of a bounty.
	/// @param workerStablePay amount of stable coin
	/// @param workerNativePay Amount of native coin
	/// @param exchangeRate Amount of native token 1 DAI can buy
	/// @param nativeToken Address of native token
	/// @param stablecoin Address of stablecoin
	/// @param dailyYieldPercentage APR/365. Basis point, the first two integers are the decimals. E.g: 0,27%=27, 30%=3000
	/// @param workerDeadline When the worker should have completed thetask (in seconds after UNIX epoch)
	/// @return Id of created bounty
	function createBounty(
		uint128 workerStablePay,
		uint128 workerNativePay,
		uint96 exchangeRate,
		address nativeToken,
		address stablecoin,
		uint128 dailyYieldPercentage,
		uint256 workerDeadline
	) external payable returns (uint256);

	/// Called by the investor to invest in bounty.
	function invest(uint256 bountyId, uint128 stableAmount) external;

	/// Called by the worker to receive payment. This also closes the investment opportunity.
	function acceptWorkerPayment(uint256 bountyId) external;

	/// Called by am investor to receive payment. Can only be called once the investment opportunity has been closed.
	function acceptInvestorPayment(uint256 bountyId) external;

	///  Marks the bounty as completed by the recruiter, unlocking withdrawal.
	function markBountyAsCompleted(uint256 bountyId) external;

	// Cancel the bounty, returning to everyone the tokens they invested
	function cancelBounty(uint256 bountyId) external;

	// Withdraw investments from a cancelled bounty
	function withdrawInvestments(uint256 bountyId) external;

	/////////////////
	// VIEW FUNCTIONS
	/////////////////

	/// Get information of a bounty.
	function getBounty(uint256 bountyId) external view returns (BountyMetadata memory);

	function getAmountOfInvestments(uint256 bountyId) external view returns (uint256);

	/// Gets an investment the sender has put in the bounty
	/// @param bountyId the id of the bounty
	/// @param investmentId the id of the investment, starting at 1, in the same order as they were created for this bounty.
	function getInvestment(uint256 bountyId, uint256 investmentId) external view returns (InvestmentMetadata memory);

	// TODO: double check with Fwoued: is the yield pool calculated on the total bounty amount or just what is in native token ?
	/// Calculates the yield pool in native token
	/// @param workerNativePay the amount of native tokens initially on the bounty
	/// @param dailyYieldPercentage: in basis point as an integer. E.g 30%=3000
	/// @param bountyCreationDate the block timestamp when the bounty was created
	/// @param workerDeadline When the worker should have completed the task (in seconds after UNIX epoch)
	function calculateYieldPool(
		uint128 workerNativePay,
		uint128 dailyYieldPercentage,
		uint256 bountyCreationDate,
		uint256 workerDeadline
	) external pure returns (uint128);

	/// Calculates the total yield earned on an investment
	/// @param initialValue the value we started with
	/// @param dailyYieldPercentage: in basis point as an integer. E.g 30%=3000
	/// @param daysBeforeInvestmentOpportunityCloses how many days before it is no longer possible to invest
	function calculateTotalYield(
		uint128 initialValue,
		uint128 dailyYieldPercentage,
		uint128 daysBeforeInvestmentOpportunityCloses
	) external pure returns (uint128);
}

contracts/BountyUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './DeadlineUtils.sol';
import './Bounty.sol';

library BountyUtils {
	function setWorkerPayToZero(BountyMetadata storage bounty) external {
		bounty.workerStablePay = 0;
		bounty.workerNativePay = 0;
	}

	function isInvestmentOpportunityClosed(BountyMetadata storage bounty, uint256 investmentOpportunityPercentage) external view returns (bool) {
		return
			bounty.status == BountyStatus.WorkerHasBeenPaid ||
			(bounty.status == BountyStatus.Completed &&
				block.timestamp >= DeadlineUtils.getInvestmentOpportunityDeadline(bounty.creationDate, bounty.workerDeadline, investmentOpportunityPercentage));
	}

}

